package it.eng.areas.ems.sdodaeservices.delegate.model;

import it.eng.areas.ems.sdodaeservices.entity.DistanceEnum;

public class Distance {

	private Double distance;
	
	private DistanceEnum unit;
	
	public Distance() {
		// TODO Auto-generated constructor stub
	}
	
	

	public Distance(Double distance, DistanceEnum unit) {
		super();
		this.distance = distance;
		this.unit = unit;
	}



	public Double getDistance() {
		return distance;
	}

	public void setDistance(Double distance) {
		this.distance = distance;
	}

	public DistanceEnum getUnit() {
		return unit;
	}

	public void setUnit(DistanceEnum unit) {
		this.unit = unit;
	}
	
	
	
	
}
